import utime as  time
import framebuf
import base64
import math

from ir_rx.oled_setup import oled as display
from config import side

yoda=''
darth=''

if side == 'dark':
    ziva = yoda
elif side == 'light':
    ziva = darth

def custom_to_buff(data):
    width = data[0]
    height = data[1]
    fbuff = framebuf.FrameBuffer(data[2:],width,height, framebuf.MONO_HLSB)
    return fbuff
      
def show_image(image):
    display.blit(image, 0, 0)
    display.show()

ziva_image = custom_to_buff(bytearray(base64.b64decode(ziva)))

display.fill(0)
show_image(ziva_image)

 